                                onChange={(event) => setTieneBombona(event.target.value)}
                            />
                            <label htmlFor="tiene_bombona_si">Sí</label>
                        </div>

                        <div className='opcion'>
                            <input
                                type="radio"
                                id="tiene_bombona_no"
                                name="tiene_bombona"
                                value="No"
                                onChange={(event) => setTieneBombona(event.target.value)}
                            />
